import { AfterViewInit, Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { WebcamComponent, WebcamImage } from 'ngx-webcam';
import { Subject, Subscription } from 'rxjs';
import { CameraService } from '../services/camera.service';

@Component({
  selector: 'app-camera-component',
  templateUrl: './camera.component.html',
  styleUrls: ['./camera.component.css']
})
export class CameraComponent implements OnInit, OnDestroy, AfterViewInit{
  @ViewChild(WebcamComponent)
  webcam!: WebcamComponent;
  width =  400;
  height = 400
  pics: string[] = []
  sub$!: Subscription
  trigger = new  Subject<void>;
  constructor(private router: Router, private cameraSvc: CameraService){
  }

  ngOnInit(): void {
      console.log("init ... " + this.webcam);
  }

  ngOnDestroy(): void {
      this.sub$.unsubscribe();
  }

  ngAfterViewInit(): void {
      this.webcam.trigger = this.trigger;
      this.sub$ = this.webcam.imageCapture.subscribe(
        this.snapshot.bind(this)
      )
  }

  snap(){
    this.trigger.next();
  }

  snapshot(webcamImg: WebcamImage){
    this.cameraSvc.imageData = webcamImg.imageData;
    this.cameraSvc.upload();
    this.pics.push(webcamImg.imageAsDataUrl);
  }
}

<div>
    <webcam [width]="width" [height]="height" [trigger]="trigger"
        (imageCapture)="snapshot($event)">
    </webcam>
</div>

<div>
    <button type="button" (click)="snap()">Snap</button>
</div>

<div *ngFor="let p of pics">
    <img [src]="p" width="100"/>
</div>
