import { AfterViewInit, Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { WebcamComponent } from 'ngx-webcam';
import { CameraService } from '../services/camera.service';

@Component({
  selector: 'app-camera-component',
  templateUrl: './camera.component.html',
  styleUrls: ['./camera.component.css']
})
export class CameraComponent implements OnInit, OnDestroy, AfterViewInit{
  @ViewChild(WebcamComponent)
  webcam!: WebcamComponent;
  width =  400;
  height = 400

  constructor(private router: Router, private cameraSvc: CameraService){

  }

  ngOnInit(): void {

  }

  ngOnDestroy(): void {

  }

  ng
}
<div>
    <webcam [width]="width" [height]="height" [trigger]="trigger"
        (imageCapture)="snapshot($event)">
    </webcam>
</div>

<div>
    <button type="button" (click)="snap()">Snap</button>
</div>

<div *ngFor="let p of pics">
    <img [src]="p" width="100"/>
</div>
